                "price": "-0.00750",
                "price_unit": "USD",
                "sid": "SM77d5ccc71419444fb730541daaaaaaaa",
                "status": "sent",
                "subresource_uris": {
                    "media": "/2010-04-01/Accounts/AC123/Messages/SM77d5ccc71419444fb730541daaaaaaaa/Media.json"
